$(document).ready(function() {
    //character counts for form fields
    $('#postTitleInput').characterCounter({
        maxlength: 70,
        blockextra: true
    });
    $('#registerInput').characterCounter({
        maxlength: 20,
        blockextra: true
    });
    $('#usernameLength').characterCounter({
        maxlength: 20,
        blockextra: true
    });

    $("input").change(function() {
        $('.myChange').removeAttr('disabled');
    });

    // $(document).ready(function() {
    //     $('textarea[data-limit-rows=true]')
    //         .on('keypress', function(event) {
    //             var textarea = $(this),
    //                 text = textarea.val(),
    //                 numberOfLines = (text.match(/\n/g) || []).length + 1,
    //                 maxRows = parseInt(textarea.attr('rows'));

    //             if (event.which === 13 && numberOfLines === maxRows) {
    //                 return false;
    //             }
    //         });
    // });
    //allow user to remove warning and alerts from screen
    $('.delete').on('click', function() {
        $('.remove_alert').remove();
    });
    //detect any changes made to form
    $('form').on('keyup change', 'input, select, textarea', function() {
        $('.myButton').removeAttr('disabled');
    });
    //if there is a textarea run below script as well
    if ($("textarea").length > 0) {
        //detect any changes made within the CKEDITOR
        for (var i in CKEDITOR.instances) {
            CKEDITOR.instances[i].on('change', function() {
                $('.myButton').removeAttr('disabled');
            });
        }

    }



});